  "tempToken": "2e9cd256-1598-41a2-ae48-41b1dd34a0b5", // celui reçu à l’étape 1
  "deviceInfo": {
    "deviceId": "device-1234",
    "deviceType": "MOBILE",
    "deviceModel": "iPhone de Mac",
    "osName":"tester"
  }
}

{
  "clientId": "30fd3085-1e39-4dc8-ba56-79074b0387fb",
  "name": "Clé API Production",
  "description": "Clé API pour l'environnement de production",
  "permissions": [
    {
      "serviceName": "user-service",
      "endpointPattern": "/api/users/*",
      "methods": ["GET", "POST", "PUT"],
      "scope": "READ_WRITE"
    },
    {
      "serviceName": "product-service",
      "endpointPattern": "/api/products/*",
      "methods": ["GET"],
      "scope": "READ_WRITE"
    }
  ],
  "rateLimit": {
    "maxRequests": 1000,
    "timeWindow": "PT1H",
    "description": "1000 requêtes par heure"
  }
}

{
    "templateCode": "EPASS_INVITATION",
    "templateName": "Notification for Invitation epass",
    "templateDescription": "Notification for Invitation epass",
    "clientId": "FAROTY",
    "notificationType": "EMAIL",
    "languageCode": "fr",
    "subjectTemplate": "Invitation ticket - ePass",
    "contentTemplate": "Invitation ticket - ePass",
    "htmlTemplate": "<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Invitation à rejoindre une organisation</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: 'Arial', sans-serif;
            background-color: #004f71;
        }
        .email-container {
            max-width: 600px;
            margin: 20px auto;
            background-color: #ffffff;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
        }
        .header {
            background-color: #ffffff;
            padding: 30px 20px;
            text-align: center;
        }
        .header img {
            max-width: 200px;
            height: auto;
        }
        .content {
            padding: 40px 30px;
            color: #000000;
        }
        .content h1 {
            color: #004f71;
            font-size: 24px;
            margin-bottom: 20px;
            margin-top: 0;
        }
        .content p {
            font-size: 16px;
            line-height: 1.6;
            margin-bottom: 20px;
        }
        .invitation-box {
            background-color: #f9f9f9;
            border: 2px solid #ffcb05;
            border-radius: 8px;
            padding: 25px;
            margin: 30px 0;
        }
        .invitation-details {
            margin: 15px 0;
        }
        .invitation-details strong {
            color: #004f71;
        }
        .cta-button {
            display: inline-block;
            background-color: #004f71;
            color: #ffffff;
            text-decoration: none;
            padding: 15px 40px;
            border-radius: 5px;
            font-size: 16px;
            font-weight: bold;
            margin: 20px 0;
            text-align: center;
        }
        .cta-button:hover {
            background-color: #003a52;
        }
        .button-container {
            text-align: center;
            margin: 30px 0;
        }
        .info-box {
            background-color: #fff9e6;
            border-left: 4px solid #ffcb05;
            padding: 15px;
            margin: 20px 0;
            font-size: 14px;
            color: #000000;
        }
        .role-badge {
            display: inline-block;
            background-color: #ffcb05;
            color: #000000;
            padding: 5px 15px;
            border-radius: 20px;
            font-size: 14px;
            font-weight: bold;
            margin: 10px 0;
        }
        .expiry-notice {
            font-size: 14px;
            color: #666;
            text-align: center;
            margin-top: 20px;
        }
        .footer {
            background-color: #ffffff;
            color: #000000;
            padding: 20px 30px;
            text-align: center;
            font-size: 14px;
            border-top: 2px solid #000000;
            margin: 0 30px;
        }
        .footer a {
            color: #004f71;
            text-decoration: none;
        }
    </style>
</head>
<body>
    <div class="email-container">
        <!-- Header avec logo -->
        <div class="header">
            <img src="{{logoUrl}}"  style="width: 100%; height: 100%; object-fit: contain;"  alt="Logo">
        </div>

        <!-- Contenu principal -->
        <div class="content">
            <h1>🎉 Vous avez été invité !</h1>

            <p>Bonjour,</p>

            <p><strong>{{invitedByName}}</strong> vous invite à rejoindre son équipe de gestion sur notre plateforme.</p>

            <div class="invitation-box">
                <div class="invitation-details">
                    <strong>Organisation :</strong> {{organizationName}}<br>
                    <strong>Invité par :</strong> {{invitedByName}}<br>
                    <strong>Rôle attribué :</strong> <span class="role-badge">{{role}}</span>
                </div>

                <p style="margin-top: 20px; font-size: 14px; color: #666;">
                    En acceptant cette invitation, vous pourrez aider à gérer les événements, les ventes de billets et d'autres aspects de l'organisation.
                </p>
            </div>

            <div class="button-container">
                <a href="{{invitationLink}}" class="cta-button">Accepter l'invitation</a>
            </div>

            <p class="expiry-notice">⏱️ Ce lien d'invitation est valide pendant <strong>7 jours</strong></p>

            <div class="info-box">
                <strong>ℹ️ Remarque :</strong> Si vous n'avez pas de compte, vous serez invité à en créer un. Si vous avez déjà un compte, connectez-vous avec vos identifiants habituels.
            </div>

            <p style="font-size: 14px; color: #666; margin-top: 30px;">
                Si vous ne souhaitez pas accepter cette invitation, vous pouvez simplement ignorer cet email.
            </p>
        </div>

        <!-- Footer -->
        <div class="footer">
            <p>&copy; 2025 Faroty. Tous droits réservés.</p>
            <p>
                <a href="https://www.faroty.com">Visitez notre site</a> | 
                <a href="mailto:support@faroty.com">Nous contacter</a>
            </p>
        </div>
    </div>
</body>
</html>"
}
